import { EditorStatus } from "../../contexts/DraggableContext";
import { CanvasItem } from "../../contexts/DraggableContext";
type StatusDropdownProps = {
  currentStatus: EditorStatus;
  onChange: (status: EditorStatus) => void;
  canvasItems: CanvasItem[];
  setCanvasItems: React.Dispatch<React.SetStateAction<CanvasItem[]>>;
};

export const StatusDropdown = ({
  currentStatus,
  onChange,
  canvasItems,
  setCanvasItems,
  onSaveAll,
}: StatusDropdownProps) => {
  const handleSaveAll = () => {
    setCanvasItems((prevItems) =>
      prevItems.map((item) => ({ ...item, status: EditorStatus.Saved }))
    );
  };

  return (
    <div>
      <select
        value={currentStatus}
        onChange={(e) => onChange(e.target.value as EditorStatus)}
      >
        <option value={EditorStatus.NotStarted}>
          {EditorStatus.NotStarted}
        </option>
        <option value={EditorStatus.Editing}>{EditorStatus.Editing}</option>
        <option value={EditorStatus.Saved}>{EditorStatus.Saved}</option>
      </select>
      <button onClick={onSaveAll}>保存</button> 
    </div>
  );
};
