import React from "react";
import { DraggableItem } from "../Generals/DraggableItem";
import { ShapeType } from "../../contexts/DraggableContext";

type Shape = {
  id: number;
  type: ShapeType;
  isSelected: boolean;

};
const DEFAULT_SHAPE_SIZES = {
  circle: { width: 40, height: 40 },
  rectangle: { width: 60, height: 30 },
  triangle: { width: 50, height: 50 },
  hexagon: { width: 50, height: 30 },
  diamond: { width: 40, height: 40 },
  parallelogram: { width: 60, height: 30 },
};

const shapes: Shape[] = [
  { id: 1, type: "circle", isSelected: false,...DEFAULT_SHAPE_SIZES.circle  },
  { id: 2, type: "rectangle", isSelected: false,...DEFAULT_SHAPE_SIZES.rectangle  },
  { id: 3, type: "triangle", isSelected: false ,...DEFAULT_SHAPE_SIZES.triangle },
  { id: 4, type: "hexagon", isSelected: false,...DEFAULT_SHAPE_SIZES.hexagon  },
  { id: 5, type: "diamond", isSelected: false ,...DEFAULT_SHAPE_SIZES.diamond },
  { id: 6, type: "parallelogram", isSelected: false,...DEFAULT_SHAPE_SIZES.parallelogram  },
];

const ShapeLibrary: React.FC = () => {
  const handleDragStart = (e: React.DragEvent, type: ShapeType, id: number) => {
    e.dataTransfer.setData("type", type);
    e.dataTransfer.setData("source", "library");
    e.dataTransfer.setData("id", id.toString());
  };

  return (
    <div className="shape-library-container">
      {shapes.map((shape) => (
        <div
        className="shape-library-item"
          draggable
          onDragStart={(e) => handleDragStart(e, shape.type, shape.id)}
          key={shape.id}
        >
          <DraggableItem
            id={shape.id}
            type={shape.type}
            isSelected={shape.isSelected}
            text=""
            defaultSize={DEFAULT_SHAPE_SIZES[shape.type]}
          />
        </div>
      ))}
    </div>
  );
};

export default ShapeLibrary;
