import React, {
  useContext,
  useState,
  createContext,
  useRef,
  useEffect,
} from "react";
import { DraggableData, DraggableEvent } from "react-draggable";

// context cho tính năng kéo thả
type DraggableContextType = {
  positions: { [key: string]: { x: number; y: number } };
  handleStop: (id: string) => (e: DraggableEvent, data: DraggableData) => void;
  handleStart: (id: string) => (e: DraggableEvent, data: DraggableData) => void;
};
const defaultDraggableValue: DraggableContextType = {
  positions: {},
  handleStop: () => () => {},
  handleStart: () => () => {},
};

const DraggableContext = createContext<DraggableContextType>(
  defaultDraggableValue
);

// custom hook để truy cập vào giá trị của Draggble context
export const useDraggble = () => {
  return useContext(DraggableContext);
};

// component để chia sẻ logic và state liên quan tới kéo thả
type DraggableProviderProps = {
  children: React.ReactNode;
};
export const DraggableProvider = ({ children }: DraggableProviderProps) => {
  const [positions, setPositions] = useState<{
    [key: string]: { x: number; y: number };
  }>({});
  const initialPositionsRef = useRef<{
    [key: string]: { x: number; y: number };
  }>({});
  const canvasRef = useRef<HTMLDivElement | null>(null);
  useEffect(() => {
    if (!initialPositionsRef.current["rectangle"]) {
      initialPositionsRef.current["rectangle"] = positions["rectangle"];
    }

    if (!initialPositionsRef.current["circle"]) {
      initialPositionsRef.current["circle"] = positions["circle"];
    }
    if (!initialPositionsRef.current["vector"]) {
      initialPositionsRef.current["vector"] = positions["vector"];
    }
  }, [positions]);

  // lưu vị trí ban đầu của hình
  const handleStart =
    (id: string) => (e: DraggableEvent, data: DraggableData) => {};

  // hàm xử lý khi việc kéo kết thúc
  const handleStop =
    (id: string) => (e: DraggableEvent, data: DraggableData) => {
      if (canvasRef.current) {
        const canvasRect = canvasRef.current.getBoundingClientRect();
        const itemRect = (e.target as HTMLElement).getBoundingClientRect();

        // kiểm tra xem hình có nằm trong vùng canvas không
        const isInsideCanvas =
          itemRect.left >= canvasRect.left &&
          itemRect.right <= canvasRect.right &&
          itemRect.top >= canvasRect.top &&
          itemRect.bottom <= canvasRect.bottom;
        if (!isInsideCanvas) {
          setPositions((prevPositons) => ({
            ...prevPositons,
            [id]: initialPositionsRef.current[id],
          }));
        } else {
          setPositions((prevPositions) => ({
            ...prevPositions,
            [id]: { x: data.x, y: data.y },
          }));
        }
      }
    };

  return (
    <DraggableContext.Provider value={{ positions, handleStop, handleStart }}>
      <div ref={canvasRef}>{children}</div>
    </DraggableContext.Provider>
  );
};
