import React, { useRef, useEffect, useState } from "react";
import { ShapeType } from "../../contexts/DraggableContext";
import { useDraggable } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: ShapeType;
  id: number;
  isSelected: boolean;
  style?: React.CSSProperties;
  text: string;
};

export const DraggableItem = ({
  type,
  isSelected,
  id,
  style,
  text,
}: DraggableItemProps) => {
  const { updateItemText } = useDraggable();
  const [isEditing, setIsEditing] = useState(false);
  const textRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (isEditing && textRef.current) {
      textRef.current.focus();
    }
  }, [isEditing]);

  const handleTextBlur = (e: React.FocusEvent<HTMLDivElement>) => {
    setIsEditing(false);
    updateItemText(id, e.currentTarget.textContent || "");
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    if (e.key === "Enter") {
      e.preventDefault();
      (e.target as HTMLElement).blur();
    }
  };

  const styles = {
    border: isSelected && !isEditing ? "1px dashed black" : "none",
    ...style,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };
  const getShapeStyles = () => {
    const baseStyles = {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      border: isSelected && !isEditing ? "2px solid blue" : "1px solid black", // làm nổi bật cạnh khi isSelected
    };

    switch (type) {
      case "circle":
        return { ...baseStyles, borderRadius: "50%" };
      case "rectangle":
        return baseStyles;
      case "triangle":
        return baseStyles;
      case "hexagon":
        return baseStyles;
      case "diamond":
        return baseStyles;
      case "parallelogram":
        return baseStyles;
      default:
        return baseStyles;
    }
  };
  return (
    <div
      style={styles}
      className="shape-container"
      onClick={() => setIsEditing(false)}
      onDoubleClick={() => setIsEditing(true)}
    >
      {type === "circle" && <div className="circle" style={getShapeStyles()}></div>}
      {type === "rectangle" && <div className="rectangle" style={getShapeStyles()}></div>}
      {type === "triangle" && <div className="triangle" style={getShapeStyles()}></div>}
      {type === "hexagon" && <div className="hexagon" style={getShapeStyles()}></div>}
      {type === "diamond" && <div className="diamond" style={getShapeStyles()}></div>}
      {type === "parallelogram" && <div className="parallelogram" style={getShapeStyles()}></div>}
      <div
        ref={textRef}
        contentEditable={isEditing}
        onBlur={handleTextBlur}
        onKeyDown={handleKeyDown}
        style={{
          outline: "none",
          cursor: "text",
          textAlign: "center",
          position: "absolute",
        }}
      >
        {text}
      </div>
    </div>
  );
};
