import React, { useRef, useEffect, useState } from "react";
import { ShapeType } from "../../contexts/DraggableContext";
import { useDraggable } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: ShapeType;
  id: number;
  isSelected: boolean;
  style?: React.CSSProperties;
  text: string;
};

export const DraggableItem = ({
  type,
  isSelected,
  id,
  style,
  text,
}: DraggableItemProps) => {
  const { updateItemText } = useDraggable();
  const [isEditing, setIsEditing] = useState(false);
  const textRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (isEditing && textRef.current) {
      textRef.current.focus();
    }
  }, [isEditing]);

  const handleTextBlur = (e: React.FocusEvent<HTMLDivElement>) => {
    setIsEditing(false);
    updateItemText(id, e.currentTarget.textContent || "");
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    if (e.key === "Enter") {
      e.preventDefault();
      (e.target as HTMLElement).blur();
    }
  };
  const shapeStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    position: "relative" as "relative",
    border: isSelected && !isEditing ? "2px solid blue" : "1px solid black",
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        position:"absolute"
      }}
    >
      {type === "circle" && (
        <div
          className="circle"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      {type === "rectangle" && (
        <div
          className="rectangle"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      {type === "triangle" && (
        <div
          className="triangle"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      {type === "hexagon" && (
        <div
          className="hexagon"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      {type === "diamond" && (
        <div
          className="diamond"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      {type === "parallelogram" && (
        <div
          className="parallelogram"
          style={shapeStyles}
          onClick={() => setIsEditing(false)}
          onDoubleClick={() => setIsEditing(true)}
        ></div>
      )}
      <div
        ref={textRef}
        contentEditable={isEditing}
        onBlur={handleTextBlur}
        onKeyDown={handleKeyDown}
        style={{
          outline: "none",
          cursor: "text",
          textAlign: "center",
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
        }}
      >
        {text}
      </div>
    </div>
  );
};
