import React, { DragEvent } from 'react'; // Thêm DragEvent từ 'react'
import { useDraggable } from '../contexts/DraggableContext';
import { DraggableItem } from './Generals/DraggableItem';
import {ShapeType} from "../contexts/DraggableContext"
export const Canvas = () => {
  const { canvasItems, addItemToCanvas } = useDraggable();

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    const type = e.dataTransfer.getData('type') as ShapeType;
    const position = { x: e.clientX, y: e.clientY };
    addItemToCanvas({ type, id: Date.now() }, position);
  };

  return (
    <div className="canvas border border-dark" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}
    style={{
      width: "500px",
      height: "500px",
      position: "relative"
    }}>
      {canvasItems.map(item => (
        <DraggableItem key={item.id} type={item.type} />
      ))}
    </div>
  );
};
