import React from 'react';
import Draggable, { DraggableEventHandler, DraggableData, DraggableEvent } from 'react-draggable';
import { ShapeType } from '../../contexts/DraggableContext';

type DraggableItemProps = {
  type: ShapeType;
  onDragStart?: () => void;
  onDragEnd?: () => void;
  isDragging?: boolean;
};

export const DraggableItem = ({ type, onDragStart, onDragEnd, isDragging }: DraggableItemProps) => {
  const handleDragStart: DraggableEventHandler = (e: DraggableEvent, data: DraggableData) => {
    // Your logic here
    if (onDragStart) onDragStart();
  };

  const handleDragEnd: DraggableEventHandler = (e: DraggableEvent, data: DraggableData) => {
    // Your logic here
    if (onDragEnd) onDragEnd();
  };

  return (
    <Draggable 
      onStart={handleDragStart}
      onStop={handleDragEnd}
      position={isDragging ? { x: 0, y: 0 } : undefined}
    >
     <div style={{cursor: 'pointer'}}>
  {type === 'circle' && <div style={{width: '50px', height: '50px', borderRadius: '50%', backgroundColor: 'blue'}}></div>}
  {type === 'rectangle' && <div style={{width: '50px', height: '30px'}}></div>}
  {/* Thêm các loại hình khác nếu cần */}
  {type}
</div>

    </Draggable>
  );
};
