import React, { useRef, useEffect, useState } from "react";
import { ShapeType } from "../../contexts/DraggableContext";
import { useDraggable } from "../../contexts/DraggableContext";
import { Resizable } from "react-resizable";
import 'react-resizable/css/styles.css';
import { ResizeCallbackData } from "react-resizable";


type DraggableItemProps = {
  type: ShapeType;
  id: number;
  isSelected: boolean;
  style?: React.CSSProperties;
  text: string;
};

export const DraggableItem = ({
  type,
  isSelected,
  id,
  style,
  text,
}: DraggableItemProps) => {
  const { updateItemText } = useDraggable();
  const [isEditing, setIsEditing] = useState(false);
  const textRef = useRef<HTMLDivElement>(null);
  const [size, setSize] = useState({ width: 100, height: 100 });

  useEffect(() => {
    if (isEditing && textRef.current) {
      textRef.current.focus();
    }
  }, [isEditing]);

  const handleTextBlur = (e: React.FocusEvent<HTMLDivElement>) => {
    setIsEditing(false);
    updateItemText(id, e.currentTarget.textContent || "");
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    if (e.key === "Enter") {
      e.preventDefault();
      (e.target as HTMLElement).blur();
    }
  };

  const handleResize = (event: React.SyntheticEvent<Element, Event>, data: ResizeCallbackData) => {
    const { width, height } = data.size;
    setSize({ width, height });
  };
  const styles: React.CSSProperties = {
  
    width: size.width,
    height: size.height,
    position: "relative",
  };
  return (
    <Resizable
      width={size.width}
      height={size.height}
      onResize={handleResize}
      resizeHandles={["nw", "n", "ne", "e", "se", "s", "sw", "w"]}
    >
      <div
        style={styles}
        className={`shape-container ${isSelected ? 'selected' : ''}`}
        onClick={() => setIsEditing(false)}
        onDoubleClick={() => setIsEditing(true)}
      >
        {type === "circle" && <div className="circle"></div>}
        {type === "rectangle" && <div className="rectangle"></div>}
        {type === "triangle" && <div className="triangle"></div>}
        {type === "hexagon" && <div className="hexagon"></div>}
        {type === "diamond" && <div className="diamond"></div>}
        {type === "parallelogram" && <div className="parallelogram"></div>}
        <div
          ref={textRef}
          contentEditable={isEditing}
          onBlur={handleTextBlur}
          onKeyDown={handleKeyDown}
          style={{
            outline: "none",
            cursor: "text",
            textAlign: "center",
            position: "absolute",
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)'
          }}
        >
          {text}
        </div>
      </div>
    </Resizable>
  );
};
