import React from "react";
import { DraggableItem } from "../components/Generals/DraggableItem";
import { ShapeType } from "../contexts/DraggableContext";

type Shape = {
  id: number;
  type: ShapeType;
};

const shapes: Shape[] = [
  { id: 1, type: "circle" },
  { id: 2, type: "rectangle" },
  { id: 3, type: "vector" },
];

const ShapeLibrary: React.FC = () => {
  const handleDragStart = (e: React.DragEvent, type: ShapeType, id: number) => {
    e.dataTransfer.setData("type", type);
    e.dataTransfer.setData("source", "library");
    e.dataTransfer.setData("id", id.toString());
  };
// Loại bỏ hình ảnh xem trước
const img = new Image();
img.src = 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mP8/wcAAwAB/epP3ZAAAAAASUVORK5CYII='; // 1x1 pixel transparent PNG
e.dataTransfer.setDragImage(img, 0, 0);
  return (
    <div>
      {shapes.map((shape) => ( 
        <div 
        draggable="true"
          onDragStart={(e) => handleDragStart(e, shape.type, shape.id)} 
          key={shape.id}
        >
          <DraggableItem 
            id={shape.id} 
            type={shape.type} 
          />
        </div>
      ))}
    </div>
  );
};


export default ShapeLibrary;
