type ResizerProp={
    onResize: (detalWidth:number, dettalheight:number)=>void;
}
export const Resizer=({onResize}:ResizerProp)=>{
    const handleMouseDown = (e: React.MouseEvent<HTMLDivElement>) => {
    const startX=e.clientX;
    const startY=e.clientY;
    const handleMoveMouse=(moveEvent:MouseEvent)=>{
        const newWidth=moveEvent.clientX-startX;
        const newHeight=moveEvent.clientY-startY;
        onResize(newWidth,newHeight)
    }
    const handleMouseUp=()=>{
        window.removeEventListener("mousemove",handleMoveMouse)
        window.removeEventListener("mouseup",handleMouseUp)
    }
    window.addEventListener("mousemove",handleMoveMouse)
    window.addEventListener("mouseup",handleMouseUp)
}
return <div onMouseDown={handleMouseDown} style={{ cursor: 'se-resize', width: '10px', height: '10px', position: 'absolute', right: 0, bottom: 0, backgroundColor: 'black' }}>

</div>
}
