import React from 'react';
import { useDraggable } from '.././contexts/DraggableContext';

export const Canvas = () => {
  const { canvasItems } = useDraggable();

  return (
    <div>
      {canvasItems.map(item => (
        <div key={item.id} style={{ position: 'absolute', top: item.position.y, left: item.position.x }}>
          {item.type}
        </div>
      ))}
    </div>
  );
};
