import React, { useCallback, useEffect, useState, useMemo ,useRef,useReducer} from "react";
import { useDraggable } from "../../contexts/DraggableContext";
import Draggable from "react-draggable";
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import { DraggableItem } from "../Generals/DraggableItem";
import { ShapeType } from "../../contexts/DraggableContext";

const DocumentViewer = React.memo(({ uri }: { uri: string }) => {
  const [isDraggingShape, setIsDraggingShape] = useState(false);

  useEffect(() => {
    const handleStartDragShape = () => {
      setIsDraggingShape(true);
    };

    const handleStopDragShape = () => {
      setIsDraggingShape(false);
    };

    window.addEventListener("dragstart", handleStartDragShape);
    window.addEventListener("dragend", handleStopDragShape);

    return () => {
      window.removeEventListener("dragstart", handleStartDragShape);
      window.removeEventListener("dragend", handleStopDragShape);
    };
  }, []);

  return (
    <div className="pdf" style={{ height: "600px", width: "100%", overflow: 'hidden', pointerEvents: isDraggingShape ? 'none' : 'auto' }}>
      <DocViewer 
        documents={[{ 
          uri: uri,
          fileType: uri.substring(uri.length - 6).split('.').pop(),
          fileName: "remote " + uri.substring(uri.length - 6).split('.').pop() + " file"
        }]} 
        pluginRenderers={DocViewerRenderers}
        theme={{
          primary: "#5296d8",
          secondary: "#ffffff",
          tertiary: "#5296d899",
          textPrimary: "#ffffff",
          textSecondary: "#5296d8",
          textTertiary: "#00000099",
          disableThemeScrollbar: true,
        }}
      />
    </div>
  );
});


const ACTIONS = {
  SET_CANVAS_ITEMS: 'set_canvas_items',
  SET_SELECTED_SHAPE_ID: 'set_selected_shape_id',
 
};
type State = {
  canvasItems: any[]; 
  selectedShapeId: number | null;
};

type Action = {
  type: string;
  payload?: any;
};

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case ACTIONS.SET_CANVAS_ITEMS:
      return { ...state, canvasItems: action.payload };
    case ACTIONS.SET_SELECTED_SHAPE_ID:
      return { ...state, selectedShapeId: action.payload };

    default:
      return state;
  }
}



const CanvasComponent = React.memo(() => {
  const { canvasItems: initialCanvasItems, addItemToCanvas, undoCanvasAction, uri } = useDraggable();
  const [state, dispatch] = useReducer(reducer, {
    canvasItems: initialCanvasItems,
    selectedShapeId: null,
  });




  const handleDrop = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    const type = e.dataTransfer.getData("type") as ShapeType;
    const source = e.dataTransfer.getData("source");
    const canvasRect = e.currentTarget.getBoundingClientRect();
    const position = {
      x: e.clientX - canvasRect.left,
      y: e.clientY - canvasRect.top,
    };
    if (source === "library") {
      addItemToCanvas({ type, id: Date.now(), isSelected: false }, position);
    }
  }, [addItemToCanvas]);

  const handleDragOver = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
  }, []);
  useEffect(() => {
    dispatch({ type: ACTIONS.SET_CANVAS_ITEMS, payload: initialCanvasItems });
  }, [initialCanvasItems]);





  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === "Delete" && state.selectedShapeId !== null) {
        dispatch({
          type: ACTIONS.SET_CANVAS_ITEMS,
          payload: state.canvasItems.filter((item) => item.id !== state.selectedShapeId)
        });
        dispatch({ type: ACTIONS.SET_SELECTED_SHAPE_ID, payload: null });
      }
      if (event.ctrlKey && event.key === "z") {
        undoCanvasAction();
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [state.selectedShapeId, undoCanvasAction]);

  const memoizedDocumentViewer = useMemo(() => {
    return <DocumentViewer uri={uri} />;
  }, [uri]);



  return (
<div
      onDrop={handleDrop}
      onDragOver={handleDragOver}
      onClick={() => dispatch({ type: ACTIONS.SET_SELECTED_SHAPE_ID, payload: null })}
      style={{
        border: "1px solid black",
        minHeight: "900px",
        position: "relative",
      }}
    >
{state.canvasItems.map((item) => (
     <Draggable
     key={item.id}
     position={{ x: item.position.x, y: item.position.y }}
     onStop={(e, data) => {
       dispatch({
         type: ACTIONS.SET_CANVAS_ITEMS,
         payload: state.canvasItems.map((i) => i.id === item.id ? { ...i, position: { x: data.x, y: data.y } } : i)
       });
     }}
   >
       <div
            style={{ position: "absolute", top: item.position.y, left: item.position.x }}
            onClick={(e) => {
              e.stopPropagation();
              dispatch({ type: ACTIONS.SET_SELECTED_SHAPE_ID, payload: item.id });
            }}
          >
            <DraggableItem
              type={item.type}
              id={item.id}
              isSelected={state.selectedShapeId === item.id}
              style={{ position: "absolute" }}
              text={item.text || ""}
            />
          </div>
        </Draggable>
      ))}
   {memoizedDocumentViewer}
    </div>
  )
});
const Canvas = React.memo(CanvasComponent);

export default Canvas;
