import React, { DragEvent } from "react";
import { useDraggable } from "../contexts/DraggableContext";
import { DraggableItem } from "./Generals/DraggableItem";
import { ShapeType } from "../contexts/DraggableContext";

export const Canvas = () => {
  const { canvasItems, addItemToCanvas } = useDraggable();
  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    const type = e.dataTransfer.getData("type") as ShapeType;
  
    // Lấy vị trí của canvas
    const canvasRect = e.currentTarget.getBoundingClientRect();
  
    // Tính toán vị trí thả dựa trên vị trí của sự kiện và vị trí của canvas
    const position = {
      x: e.clientX - canvasRect.left,
      y: e.clientY - canvasRect.top,
    };
  
    addItemToCanvas({ type, id: Date.now() }, position);
  };
  

  return (
    <div
    onDrop={handleDrop}
    onDragOver={handleDragOver}
    style={{
      border: "1px solid black",
      minHeight: "400px",
      position: "relative",
    }}
  >
    {canvasItems.map((item) => (
      <DraggableItem 
        key={item.id} 
        type={item.type} 
        style={{ position: 'absolute', left: item.position.x, top: item.position.y }}
      />
    ))}
  </div>
  
  );
};
