import React, {
  useContext,
  useState,
  createContext,
  useRef,
  useCallback,
} from "react";

// context cho tính năng kéo thả
type DraggableContextType = {
  position: { x: number; y: number };
  handleStop: (e: any, data: any) => void;
};

const DraggableContext = createContext<DraggableContextType | null>(null);

// custom hook để truy cập vào giá trị của Draggble context
export const useDraggble = () => {
  return useContext(DraggableContext);
};

// component để chia sẻ logic và state liên quan tới kéo thả
export const DraggableProvider: React.FC = ({ children }) => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const canvasRef = useRef(null);

  // hàm xử lý khi việc kéo kết thúc
  const handleStop = useCallback(
    (e, data) => {
      if (canvasRef.current) {
        // lấy thông tin vị trí và kích thước của canvas
        // xác đinh xem hình đang đc kéo hay thả vào canvas hay không
        const canvasRect = canvasRef.current.getBoundingClientRect();
        //lấy thông tin về vị trí và kích thước hình đang được kéo
        const itemRect = e.target.getBoundingClientRect();

        // kiểm tra xem hình có nằm ngoài canvas không
        if (
          itemRect.left < canvasRect.left ||
          itemRect.right > canvasRect.right ||
          itemRect.top < canvasRect.top ||
          itemRect.bottom > canvasRect.bottom
        ) {
          setPosition({ x: 0, y: 0 });
        }
      }
    },
    [setPosition, canvasRef]
  );

  return (
    <DraggableContext.Provider value={{ position, handleStop }}>
      <div ref={canvasRef}>{children}</div>
    </DraggableContext.Provider>
  );
};
