import React from 'react';
import { useDrop } from 'react-dnd';
import { useDraggable, CanvasItem, ShapeType } from '../contexts/DraggableContext';
import { DraggableItem } from '../components/Generals/DraggableItem';

export const Canvas: React.FC = () => {
  const { canvasItems, addItemToCanvas } = useDraggable();

  const [, ref] = useDrop({
    accept: 'SHAPE',
    drop: (item: { id: number; type: ShapeType }, monitor) => {
      const offset = monitor.getClientOffset();
      addItemToCanvas(item, { x: offset.x, y: offset.y });
    },
  });

  return (
    <div ref={ref} style={{ height: '500px', width: '500px', border: '1px solid black' }}>
      {canvasItems.map((item: CanvasItem) => (
        <div key={item.id} style={{ position: 'absolute', left: item.position?.x, top: item.position?.y }}>
          <DraggableItem id={item.id} type={item.type} />
        </div>
      ))}
    </div>
  );
};
