import React from "react";
import { useDraggble } from "../contexts/DraggableContext";
import { SHAPES } from "../utils/shapesData";
export type CanvasItem = {
  type: string;
  id: number;
  position: {
    x: number;
    y: number;
  };
};

export const Canvas = () => {
  const { canvasItems, canvasRef } = useDraggble();

  console.log("Rendering Canvas with items:", canvasItems);
  return (
    <div
      ref={canvasRef}
      className="border p-2"
      style={{ width: "900px", height: "700px", position: "relative" }}
    >
{canvasItems.map((item: CanvasItem) => (
  <div key={item.id} style={{ ...SHAPES[item.type], position: 'absolute', top: item.position.y, left: item.position.x }}>
    {item.type}
  </div>
))}


    </div>
  );
};
