import React, { useRef, useEffect, useState } from "react";
import { ShapeType } from "../../contexts/DraggableContext";
import { useDraggable } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: ShapeType;
  id: number;
  isSelected: boolean;
  style?: React.CSSProperties;
  text: string;
};

export const DraggableItem = ({
  type,
  isSelected,
  id,
  style,
  text,
}: DraggableItemProps) => {
  const { updateItemText } = useDraggable();
  const [isEditing, setIsEditing] = useState(false);
  const textRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (isEditing && textRef.current) {
      textRef.current.focus();
    }
  }, [isEditing]);

  const handleTextBlur = (e: React.FocusEvent<HTMLDivElement>) => {
    setIsEditing(false);
    updateItemText(id, e.currentTarget.textContent || "");
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    if (e.key === "Enter") {
      e.preventDefault();
      (e.target as HTMLElement).blur();
    }
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
      onClick={() => setIsEditing(false)}
      onDoubleClick={() => setIsEditing(true)}
    >
      {type === "circle" && (
        <div
          className="circle"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border:
              isSelected && !isEditing ? "2px solid blue" : "1px solid black",
          }}
        ></div>
      )}
      {type === "rectangle" && (
        <div
          className="rectangle"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border:
              isSelected && !isEditing ? "2px solid blue" : "1px solid black",
          }}
        ></div>
      )}
      {type === "triangle" && (
        <div
          className="triangle"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border:
              isSelected && !isEditing ? "2px solid blue" : "1px solid black",
          }}
        ></div>
      )}
      {type === "hexagon" && (
        <div
          className="hexagon"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border:
              isSelected && !isEditing ? "2px solid blue" : "1px solid black",
          }}
        ></div>
      )}
      {type === "diamond" && (
        <div
          className="diamond"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border:
              isSelected && !isEditing ? "2px solid blue" : "1px solid black",
          }}
        ></div>
      )}
      {type === "parallelogram" && (
        <div className="parallelogram"      style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border:
            isSelected && !isEditing ? "2px solid blue" : "1px solid black",
        }}></div>
      )}
      <div
        ref={textRef}
        contentEditable={isEditing}
        onBlur={handleTextBlur}
        onKeyDown={handleKeyDown}
        style={{
          outline: "none",
          cursor: "text",
          textAlign: "center",
          position: "absolute",
        }}
      >
        {text}
      </div>
    </div>
  );
};
