import React from "react";
import { DraggableEvent, DraggableData } from "react-draggable";
import { ShapeType } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: ShapeType;
  id: number;
  onDragStart?: () => void;
  onDragEnd?: () => void;
  isDragging?: boolean;
  style?: React.CSSProperties;
};
export const DraggableItem = ({
  type,
  onDragStart,
  onDragEnd,
  isDragging,
  style,
  id,
}: DraggableItemProps) => {
  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData("type", type);
    e.dataTransfer.setData("source", "library");
    if (onDragStart) onDragStart();
  };

  const handleCanvasItemDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData("type", type);
    e.dataTransfer.setData("source", "canvas");
    e.dataTransfer.setData("id", id.toString());
  };

  const handleDragEnd = (e: DraggableEvent, data: DraggableData) => {
    if (onDragEnd) onDragEnd();
  };

  return (
    <div
      draggable
      onDragStart={handleCanvasItemDragStart} // Sửa chỗ này
      style={{ cursor: "pointer" }}
    >
      {type === "circle" && (
        <div
          style={{
            width: "50px",
            height: "50px",
            borderRadius: "50%",
            border: "1px solid black",
          }}
        ></div>
      )}
      {type === "rectangle" && (
        <div
          style={{ width: "50px", height: "30px", border: "1px solid black" }}
        ></div>
      )}
    </div>
  );
};
