import React from "react";
import "./App.css";
import { Container, Row, Col,  } from "react-bootstrap";
import { Canvas } from "./components/Canvas";
import { Generals } from "./components/Generals/generals";
import { DraggableProvider } from "./contexts/DraggableContext";
import { useDraggble } from "./contexts/DraggableContext";

function App() {
  const { handleDropOnCanvas } = useDraggble();

  return (
    <DraggableProvider>
    <Container fluid className="h-100">
      <Row className="h-100">
        {/* thanh công cụ */}
        <Col xs={2} className="border-right">
          <h3 className="text-center mb-3">Generals</h3>
         <Generals/>
        </Col>
        {/* khung làm việc */}
        <Col
          xs={8}
          className="d-flex align-items-center justify-content-center "
          onDrop={(e) => {
            e.preventDefault();
            const itemType = e.dataTransfer.getData("itemType");
            const canvasRect = e.currentTarget.getBoundingClientRect();
            if (e.clientY >= canvasRect.top && e.clientY <= canvasRect.bottom && e.clientX >= canvasRect.left && e.clientX <= canvasRect.right) {
              handleDropOnCanvas(itemType);
            }
          }}
          onDragOver={(e) => e.preventDefault()}
        >
          <Canvas/>
        </Col>
        {/* thanh thuộc tính */}
        <Col xs={2} className="border-left">
          <div id="textPropertites" >
            <h3>Text Propertites</h3>
          </div>
        </Col>
      </Row>
    </Container>
    </DraggableProvider>
  );
}

export default App;
