import React from 'react';
import Draggable from 'react-draggable';
import { useDraggable } from '../../contexts/DraggableContext';
import { DraggableEvent, DraggableData } from 'react-draggable';

type DraggableItemProps = {
  type: 'rectangle' | 'circle' | 'vector';
};
export const DraggableItem = ({ type }:DraggableItemProps) => {
  const { addItemToCanvas } = useDraggable();

  const handleStop = (e: DraggableEvent, data: DraggableData) => {
    // Logic to check if the item is inside the canvas
    // If it is, call addItemToCanvas
    addItemToCanvas({ type, id: Date.now() }, { x: data.x, y: data.y });
  };

  return (
    <Draggable onStop={handleStop}>
      <div>
        {type}
      </div>
    </Draggable>
  );
};
