import { EditorStatus } from "../../contexts/DraggableContext";
import { useState } from "react";
 type StatusDropdownProps = {
  currentStatus: EditorStatus;
  onChange: (status: EditorStatus) => void;
};

export const StatusDropdown = ({
  currentStatus,
  onChange,
}: StatusDropdownProps) => {
    const handleSaveAll = () => {
        // Logic để lưu tất cả các thay đổi
        console.log("Đã lưu tất cả");
      
        // Cập nhật trạng thái của tất cả các items thành Saved
        setCanvasItems(prevItems => prevItems.map(item => ({ ...item, status: EditorStatus.Saved })));
      };
      
  return (
<div>
      <select
        value={currentStatus}
        onChange={(e) => onChange(e.target.value as EditorStatus)}
      >
        <option value={EditorStatus.NotStarted}>{EditorStatus.NotStarted}</option>
        <option value={EditorStatus.Editing}>{EditorStatus.Editing}</option>
        <option value={EditorStatus.Saved}>{EditorStatus.Saved}</option>
      </select>
      <button onClick={handleSaveAll}>保存</button>
    </div>
  );
};
