import React, { createContext, useContext, useState } from 'react';

export type ShapeType = 'circle' | 'rectangle' | 'vector';

export type CanvasItem = {
  id: number;
  type: ShapeType;
  position?: { x: number; y: number };
};

type DraggableContextType = {
  canvasItems: CanvasItem[];
  addItemToCanvas: (item: CanvasItem, position: { x: number; y: number }) => void;
};

const DraggableContext = createContext<DraggableContextType | undefined>(undefined);

export const DraggableProvider: React.FC = ({ children }) => {
  const [canvasItems, setCanvasItems] = useState<CanvasItem[]>([]);

  const addItemToCanvas = (item: CanvasItem, position: { x: number; y: number }) => {
    setCanvasItems((prev) => [...prev, { ...item, position }]);
  };

  return (
    <DraggableContext.Provider value={{ canvasItems, addItemToCanvas }}>
      {children}
    </DraggableContext.Provider>
  );
};

export const useDraggable = () => {
  const context = useContext(DraggableContext);
  if (!context) {
    throw new Error('useDraggable must be used within a DraggableProvider');
  }
  return context;
};
