import { useDraggble } from "../contexts/DraggableContext";
 export const Canvas = () => {
    const { canvasItems } = useDraggble();
    return (
        <div className="border p-2">
        <canvas
          id="canvas"
          width="900"
          height="700"
          style={{ display: 'block', margin: 'auto', backgroundColor: 'white' }}
        ></canvas>
        {canvasItems.map((item: { type: string; id: number }) => (
          <div key={item.id}>{item.type}</div>
        ))}
      </div>
    );
};
