import React, { createContext, useState, useContext } from 'react';

// Định nghĩa kiểu cho một mục trên canvas
type CanvasItem = {
  type: string;
  id: number;
  position: { x: number; y: number };
};

type DraggableContextType = {
  canvasItems: CanvasItem[]; // Sử dụng kiểu CanvasItem đã định nghĩa
  addItemToCanvas: (item: { type: string; id: number }, position: { x: number; y: number }) => void;
};

const defaultContextValue: DraggableContextType = {
  canvasItems: [],
  addItemToCanvas: () => {},
};

export const DraggableContext = createContext<DraggableContextType>(defaultContextValue);

type DraggableProviderProps = {
  children: React.ReactNode;
};

export const DraggableProvider = ({ children }: DraggableProviderProps) => {
  const [canvasItems, setCanvasItems] = useState<CanvasItem[]>([]); // Xác định rõ kiểu cho useState

  // Hàm này sẽ thêm một mục mới vào danh sách canvasItems
  const addItemToCanvas = (item: { type: string; id: number }, position: { x: number; y: number }) => {
    const newItem: CanvasItem = { ...item, position }; // Xác định rõ kiểu cho newItem
    setCanvasItems(prevItems => [...prevItems, newItem]);
  };

  return (
    <DraggableContext.Provider value={{ canvasItems, addItemToCanvas }}>
      {children}
    </DraggableContext.Provider>
  );
};

export const useDraggable = () => {
  return useContext(DraggableContext);
};
