import { SHAPES } from "../../utils/shapesData";

type DraggableItemType = {
  type: "rectangle" | "circle" | "vector";
};

export const DraggableItem = ({ type }: DraggableItemType) => {

  const shapeStyle = SHAPES[type];
  const className =
    "mb-2 shape d-flex justify-content-center align-items-center";

  const styles: React.CSSProperties = {
    border: "1px solid black",
    textAlign: "center" as "center",
    lineHeight: shapeStyle.height,
    ...shapeStyle,
  };

  return (
    <div
      draggable
      onDragStart={(e) => {
        e.dataTransfer.setData("text/plain", type);
      }}
      className={className}
      style={styles}
    >
      {type}
    </div>
  );
};
