import React from 'react';
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import {useState} from "react"
type FileViewerProps = {
    uri: string;
    onDrop: (e: React.DragEvent<HTMLDivElement>) => void;
    onDragOver: (e: React.DragEvent<HTMLDivElement>) => void;
  };
  
  const FileViewer: React.FC<FileViewerProps> = ({ uri, onDrop, onDragOver }) => {
    const [shapesOnFile, setShapesOnFile] = useState([]);
    
  return (
    <div
      onDrop={onDrop}
      onDragOver={onDragOver}
      style={{ position: 'relative', height: '600px', width: '100%' }}
    >
      {uri && (
        <DocViewer 
          documents={[{ 
            uri: uri,
            fileType: uri.substring(uri.length - 6).split('.').pop(),
            fileName: "remote " + uri.substring(uri.length - 6).split('.').pop() + " file"
          }]} 
          pluginRenderers={DocViewerRenderers}
          theme={{
            primary: "#5296d8",
            secondary: "#ffffff",
            tertiary: "#5296d899",
            textPrimary: "#ffffff",
            textSecondary: "#5296d8",
            textTertiary: "#00000099",
            disableThemeScrollbar: true,
          }}
        />
      )}
    </div>
  );
};

export default FileViewer;
