import React, { useRef } from 'react';
import { useDraggble } from '../contexts/DraggableContext';
import { SHAPES } from '../utils/shapesData';
export const Canvas = () => {

  const canvasRef = useRef<HTMLDivElement | null>(null); 
    const { canvasItems } = useDraggble();
    return (
        <div className="border p-2" ref={canvasRef}>
           <div className="border p-2" style={{ width: '900px', height: '700px', position: 'relative', backgroundColor: 'lightgray' }}>
           {canvasItems.map((item: { type: string; id: number }) => (
                <div key={item.id} style={{ ...SHAPES[item.type], position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
                    {item.type}
                </div>
            ))}
            </div>
        </div>
    );
};
