import Draggable from "react-draggable";
import { Card } from "react-bootstrap";
import { useDraggable } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: 'rectangle' | 'circle' | 'vector';
};

export const DraggableItem = ({ type }: DraggableItemProps) => {
  const { addItemToCanvas } = useDraggable();

  const handleStop = (e, data) => {
    addItemToCanvas({ type, id: Date.now() }, { x: data.x, y: data.y });
  };

  return (
    <Draggable onStop={handleStop}>
      <div className="p-2">
        {type === 'circle' && <Card className="mb-2 rounded-circle">
          <Card.Body className="text-center"></Card.Body>
        </Card>}
        {type === 'rectangle' && <Card className="mb-2">
          <Card.Body className="text-center">Rectangle</Card.Body>
        </Card>}
        {/* Add other shapes as needed */}
      </div>
    </Draggable>
  );
};
