import { DraggableProvider } from "./contexts/DraggableContext";
import { Canvas } from "./components/Canvas/Canvas";
import ShapeLibrary from "./components/ShapeLibrary/ShapeLibrary";
import "./App.css";
import { StatusDropdown } from "./components/Status/StatusDropdown";
import React, { useState } from "react";
import { EditorStatus } from "./contexts/DraggableContext";



function App() {
  const [selectedStatus, setSelectedStatus] = useState<EditorStatus>(EditorStatus.NotStarted);


  return (
    <DraggableProvider>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-2 bg-light border-right">
            <ShapeLibrary />
          </div>

          <div className="col-md-7 border-right">
            <Canvas />
          </div>

          <div className="col-md-3 bg-light">
            <StatusDropdown 
         currentStatus={selectedStatus}
         onChange={setSelectedStatus}
            />
          </div>
        </div>
      </div>
    </DraggableProvider>
  );
}

export default App;
