import React from "react";
import Draggable, {
  DraggableEventHandler,
  DraggableData,
  DraggableEvent,
} from "react-draggable";
import { ShapeType } from "../../contexts/DraggableContext";

type DraggableItemProps = {
  type: ShapeType;
  onDragStart?: () => void;
  onDragEnd?: () => void;
  isDragging?: boolean;
  style?: React.CSSProperties; 
};

export const DraggableItem = ({
  type,
  onDragStart,
  onDragEnd,
  isDragging,
}: DraggableItemProps) => {
  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData("type", type);
    if (onDragStart) onDragStart();
  };
  const handleDragEnd: DraggableEventHandler = (
    e: DraggableEvent,
    data: DraggableData
  ) => {
    // Your logic here
    if (onDragEnd) onDragEnd();
  };

  return (
    <Draggable 
    onStart={handleDragStart}
    onStop={handleDragEnd}
    position={isDragging ? { x: 0, y: 0 } : undefined}
  >
    <div style={{cursor: 'pointer', ...style}}> {/* Áp dụng style ở đây */}
      {type === 'circle' && <div style={{width: '50px', height: '50px', borderRadius: '50%', border: '1px solid black'}}></div>}
      {type === 'rectangle' && <div style={{width: '50px', height: '30px', border: '1px solid black'}}></div>}
    </div>
  </Draggable>
  );
};
