// DraggableItem.tsx
import Draggable from 'react-draggable';
import { SHAPES } from '../../utils/shapesData';
import { useDraggble } from '../../contexts/DraggableContext';

type DraggableItemType = {
    type: 'rectangle' | 'circle' | 'vector'; 
};

export const DraggableItem = ({ type }: DraggableItemType) => {
    const { handleDropOnCanvas } = useDraggble();
    const shapeStyle = SHAPES[type];
    const className = "mb-2 shape d-flex justify-content-center align-items-center";
    
    const styles: React.CSSProperties = {
        border: '1px solid black',
        textAlign: 'center' as 'center',
        lineHeight: shapeStyle.height,
        ...shapeStyle
    };
    
    return (
        <div
          draggable
          onDragStart={(e) => {
            e.dataTransfer.setData("text/plain", type);
            console.log("Dragging started with type:", type);
          }}
          className={className}
          style={styles}
        >
          {type}
        </div>
      );
      
};
